// CSS
import "./App.css";
import "./allPages/styles/AllPages.css";
import "./allPages/styles/Navbar.css";
import "./allPages/styles/Forms.css";

// Javascript & React Components
import Logo from "./allPages/assets/bvcLogo.svg";
import { useState } from "react";
import AdminMain from "./bvcAdmin/AdminMain";
import StudentMain from "./bvcStudent/StudentMain";

function App() {
  const [loginMode, setLoginMode] = useState("student");

  const changeloginMode = () => {
    setLoginMode("admin");
  };

  if (loginMode === "student") {
    return (
      <div>
        <header className="navbar">
          <div className="leftSide">
            <img src={Logo} />
            <h1>Log In</h1>
          </div>
        </header>

        <div className="LogIn">
          <h1>Student Log In</h1>
          <form>
            <label>Email (Required)</label>
            <input type="text" required></input>
            <label>Password (Required)</label>
            <input type="text" required></input>
            <button>Done</button>
          </form>
        </div>

        <div className="special">
          <button onClick={changeloginMode}>An Administrator?</button>
        </div>
      </div>
    );
  } else if (loginMode === "admin") return <AdminMain />;
}

export default App;
