import "./App.css";
import "./allPages/styles/Navbar.css";
import "./allPages/styles/Forms.css";

// Javascript & React Components
import Logo from "./allPages/assets/bvcLogo.svg";
import { useState } from "react";
import AdminMain from "./bvcAdmin/AdminMain";
import StudentMain from "./bvcStudent/StudentMain";

function App() {
  const [loginMode, setLoginMode] = useState("student");

  const changeloginMode = () => {
    setLoginMode("admin");
  };

  return (
    <div>
      <header className="navbar">
        <div className="leftSide">
          <img src={Logo} />
          <h1>Log In</h1>
        </div>
      </header>

      <div className="LogIn">
        <h1>Add Course</h1>
        <form onSubmit={handleSubmit}>
          <label>Course Name (Required)</label>
          <input
            type="text"
            value={cName}
            onChange={(newName) => cNameSet(newName.target.value)}
            required
          ></input>
          <label>Course Code (Required)</label>
          <input
            type="text"
            value={cCode}
            onChange={(newCode) => cCodeSet(newCode.target.value)}
            required
          ></input>
          <label>Start Date (Required)</label>
          <input
            type="date"
            value={cDateStart}
            onChange={(cDateStart) => cDateStartSet(cDateStart.target.value)}
            required
          ></input>
          <label>End Date (Required)</label>
          <input
            type="date"
            value={cDateEnd}
            onChange={(cDateEnd) => cDateEndSet(cDateEnd.target.value)}
            required
          ></input>
          <button>Done</button>
        </form>
      </div>
    </div>

    // <div>
    //   <h1>Administrator Login</h1>
    //   <button>Administrator Login</button>
    // </div>
  );
}

export default App;
